import React, { useState, createContext, useContext } from "react";
import { ScoreBoard, Board, ResetButton } from "./component/index";
import "./App.css";

function App() {
  const [xPlaying, setXPlaying] = useState(true);
  const [squares, setSquares] = useState(Array(9).fill(null));
  const [scores, setScores] = useState({ xScore: 0, oScore: 0 });
  const [gameOver, setGameOver] = useState(false);

  const ThemeContext = createContext({
    squares,
    scores,
    xPlaying,
    gameOver,
    selectSquare,
    restart,
  });

  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];

  const selectSquare = (boxIdx) => {
    //Step1 : Update the board
    const updatedBoard = squares.map((value, idx) => {
      if (idx === boxIdx) {
        return xPlaying ? "X" : "O";
      } else {
        return value;
      }
    });

    setSquares(updatedBoard);

    //Step 2: Check if either player has win the game
    const winner = calculateWinner(updatedBoard);

    if (winner) {
      if (winner === "O") {
        let { oScore } = scores;
        oScore += 1;
        setScores({ ...scores, oScore });
      } else {
        let { xScore } = scores;
        xScore += 1;
        setScores({ ...scores, xScore });
      }
    }

    //Step 3: Change active player
    setXPlaying(!xPlaying);
  };

  const calculateWinner = (squares) => {
    for (let i = 0; i < lines.length; i++) {
      const [a, b, c] = lines[i];

      if (
        squares[a] &&
        squares[a] === squares[b] &&
        squares[b] === squares[c]
      ) {
        setGameOver(true);
        return squares[a];
      }
    }
  };

  const restart = () => {
    setGameOver(false);
    setSquares(Array(9).fill(null));
  };

  return (
    <div className="App">
      <ScoreBoard scores={scores} xPlaying={xPlaying} />
      <Board squares={squares} onClick={gameOver ? restart : selectSquare} />
      <ResetButton restart={restart} />
    </div>
  );
}

export default App;
