import React, { useContext } from "react";
import PropsContext from "../PropsContext";

const ScoreBoard = () => {
  const scores = useContext(PropsContext);
  const xPlaying = useContext(PropsContext);
  const { xScore, oScore } = scores;

  return (
    <div className="flex flex-row items-center justify-between px-10 py-5 my-5 text-xl font-bold border border-gray-100 rounded-md shadow-xl">
      <span className={`score x-score ${!xPlaying && "inactive"}`}>
        X - {xScore}
      </span>
      <span className={`score o-score ${!xPlaying && "inactive"}`}>
        O - {oScore}
      </span>
    </div>
  );
};

export default ScoreBoard;
